SELECT t.id,t.Account, t.idCase,c.AmountPayment, c.AmountPayment AS AmountPayment2,ps.ENP, c.rf_idV006, c.id AS rf_idCase 
INTO #tmpPeople
FROM dbo.t_File f INNER JOIN dbo.t_RegistersAccounts a ON
			f.id=a.rf_idFiles				
				INNER JOIN dbo.t_RecordCasePatient r ON
		a.id=r.rf_idRegistersAccounts				
				INNER JOIN dbo.t_PatientSMO ps ON
		r.id=ps.rf_idRecordCasePatient              
				INNER JOIN dbo.t_Case c  ON
		r.id=c.rf_idRecordCasePatient	
				INNER JOIN #tID t ON
		a.Account=t.Account
		AND c.idRecordCase=t.idCase																						
WHERE f.DateRegistration>=@dtStart AND f.DateRegistration<@dtEnd AND a.ReportYear=@reportYear AND a.ReportMonth>=1 AND a.ReportMonth<13 AND f.CodeM='141024'
		AND c.DateEnd>='20170101' AND c.DateEnd<'20180101'

UPDATE p SET p.AmountPayment=p.AmountPayment-r.AmountDeduction
FROM #tmpPeople p INNER JOIN (SELECT c.rf_idCase,SUM(c.AmountDeduction) AS AmountDeduction
								FROM dbo.t_PaymentAcceptedCase2 c
								WHERE c.DateRegistration>=@dtStart AND c.DateRegistration<@dtEnd
								GROUP BY c.rf_idCase
							) r ON
			p.rf_idCase=r.rf_idCase

SELECT  p.id ,
        p.Account ,
        cast(p.AmountPayment AS money) AS AmountPayment,
        p.ENP,
		CASE WHEN pp.INDX IS NULL THEN '' ELSE pp.INDX+', ' END
		+ CASE WHEN pp.RNNAME IS NULL THEN '' ELSE pp.RNNAME+', ' END
		+CASE WHEN pp.NP IS NULL THEN '' ELSE pp.NP+', ' END + 'ул.'
		+CASE WHEN pp.UL IS NULL THEN '' ELSE pp.UL+', ' END + 'дом.'+
		CASE WHEN pp.DOM IS NULL THEN '' ELSE pp.DOM END +  ' '
		+CASE WHEN pp.KV IS NULL THEN '' ELSE ', кв.'+pp.KV END AS Addres       
FROM #tmpPeople p LEFT JOIN PolicyRegister.dbo.PEOPLE pp ON
		p.ENP=pp.ENP
ORDER BY p.id
